&lt;span class=&#34;line&#34;&gt;68&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;69&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;70&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;71&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;encrypt_block&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;v, k&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    delta, mask = &lt;span class=&#34;number&#34;&gt;0x9e3779b9&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0xffffffff&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    sum_val = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    rounds = &lt;span class=&#34;number&#34;&gt;32&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    v0, v1 = v[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], v[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    k0, k1, k2, k3 = k[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], k[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], k[&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;], k[&lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; _ &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(rounds):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        sum_val = (sum_val + delta) &amp;amp; mask&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        v0 += ((v1 &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;) + k0) ^ (v1 + sum_val) ^ ((v1 &amp;gt;&amp;gt; &lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;) + k1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        v0 &amp;amp;= mask&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        v1 += ((v0 &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;) + k2) ^ (v0 + sum_val) ^ ((v0 &amp;gt;&amp;gt; &lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;) + k3)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        v1 &amp;amp;= mask&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; [v0, v1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;decrypt_block&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;v, k&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    delta, mask = &lt;span class=&#34;number&#34;&gt;0x9e3779b9&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;0xffffffff&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    sum_val = (delta &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;) &amp;amp; mask&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    rounds = &lt;span class=&#34;number&#34;&gt;32&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    v0, v1 = v[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], v[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    k0, k1, k2, k3 = k[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;], k[&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;], k[&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;], k[&lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;sp